"use strict";var statsContainer=document.querySelector(".project-stats-bars").getBoundingClientRect(),statsContainerPosition=statsContainer.top-window.innerHeight/2,progressBars=document.querySelectorAll(".project-stats-bar"),triggered=!1;function triggerBars(){triggered=!0;for(var e=0;e<progressBars.length;e++){var t=progressBars[e].querySelector("span"),r=progressBars[e].querySelector("h3");t.classList.add("active"),countUp(r,1e3)}}function countUp(e,t){var r=1e3/60,s=e.dataset.range,n=s/(t/r),o=0;if(NaN!==s)var a=setInterval(function(){o>=s?clearInterval(a):(o+=n,e.innerHTML=Math.floor(o)+"%")},r);else console.log("Invalid input for count sequence")}console.log(statsContainerPosition),window.addEventListener("scroll",function(e){triggered||0===progressBars.length||window.pageYOffset>=statsContainerPosition&&triggerBars()});