"use strict";function renderPosts(e){var t="";for(var r in e){t+=postFormat(e[r])}tweetContainer.innerHTML=t}function postFormat(e){var t=e.permalink,r=e.created_at,s=e.text;return'<div class="social-feed-twitter-post">\n            <a href="'+t+'" target="_blank"></a>\n            <h3>@AMPstudioUK '+(e.retweeted_status?"Retweeted":"Posted")+" "+(e.retweeted_status?"@"+e.retweeted_status.user.screen_name+" ":"")+"â€¢ "+r+"</h3>\n            <p>"+s+"</p>\n          </div>"}var xhr=new XMLHttpRequest;xhr.open("GET","https://api.amp.studio/twitter",!0),xhr.onload=function(e){if(4===xhr.readyState)if(200===xhr.status){renderPosts(JSON.parse(xhr.responseText))}else console.error(xhr.statusText)},xhr.onerror=function(e){console.error(xhr.statusText)},xhr.send(null);var tweetContainer=document.getElementById("twitter-feed");