"use strict";var headerFade=document.querySelectorAll(".header-fade");window.innerWidth>600?window.addEventListener("scroll",function(){for(var e=window.pageYOffset/window.innerHeight*1.8,t=0;t<headerFade.length;t++)headerFade[t].style.opacity=1-e},!1):window.addEventListener("scroll",function(){for(var e=window.pageYOffset/window.innerHeight*2.5,t=0;t<headerFade.length;t++){if(headerFade[t].classList.contains("page-header"))return;headerFade[t].style.opacity=1-e}},!1);var scrollButton=document.querySelector(".scroll-down-button"),scrollButtonContainer=scrollButton.getBoundingClientRect(),scrollButtonPos=scrollButtonContainer.top-window.innerHeight/3;window.addEventListener("scroll",function(){window.scrollY>scrollButtonPos?scrollButton.classList.add("active"):scrollButton.classList.remove("active")});