"use strict";window.addEventListener("load",function(){var e=document.querySelectorAll(".sticky-nav-animation");window.innerWidth>960&&window.addEventListener("scroll",function(){if(window.pageYOffset>window.innerHeight)for(t=0;t<e.length;t++)e[t].classList.add("active");else for(var t=0;t<e.length;t++)e[t].classList.remove("active")});var t=document.querySelector(".mobile-menu"),i=document.querySelector(".main-nav-list");t.addEventListener("click",function(){t.classList.toggle("active"),i.classList.toggle("active")})});